package dev.whyoleg.cryptography.bigint

import kotlin.test.*

class BigIntCompareTest {

    @Test
    fun testCompareWithInt() {
        val values = listOf(0, 1, 5, 10, Int.MAX_VALUE, Int.MIN_VALUE, 1231231238).flatMap { listOf(it, -it) }
        values.forEach { a ->
            values.forEach { b ->
                run {
                    val expected = a.compareTo(b)
                    val aFromNumber = a.toBigInt()
                    val aFromString = a.toString().toBigInt()
                    val bFromNumber = b.toBigInt()
                    val bFromString = b.toString().toBigInt()
                    assertEquals(expected, aFromString.compareTo(b))
                    assertEquals(expected, aFromString.compareTo(bFromString))
                    assertEquals(expected, aFromString.compareTo(bFromNumber))
                    assertEquals(expected, aFromNumber.compareTo(b))
                    assertEquals(expected, aFromNumber.compareTo(bFromString))
                    assertEquals(expected, aFromNumber.compareTo(bFromNumber))
                }
                run {
                    val expected = b.compareTo(a)
                    val aFromNumber = a.toBigInt()
                    val aFromString = a.toString().toBigInt()
                    val bFromNumber = b.toBigInt()
                    val bFromString = b.toString().toBigInt()
                    assertEquals(expected, bFromString.compareTo(a))
                    assertEquals(expected, bFromString.compareTo(aFromString))
                    assertEquals(expected, bFromString.compareTo(aFromNumber))
                    assertEquals(expected, bFromNumber.compareTo(a))
                    assertEquals(expected, bFromNumber.compareTo(aFromString))
                    assertEquals(expected, bFromNumber.compareTo(aFromNumber))
                }
            }
        }
    }

    @Test
    fun testCompareWithLong() {
        val values = listOf(0L, 1L, 5L, 10L, Long.MAX_VALUE, Long.MIN_VALUE, 1231231238L, 1234567890123456789L).flatMap { listOf(it, -it) }
        values.forEach { a ->
            values.forEach { b ->
                run {
                    val expected = a.compareTo(b)
                    val aFromNumber = a.toBigInt()
                    val aFromString = a.toString().toBigInt()
                    val bFromNumber = b.toBigInt()
                    val bFromString = b.toString().toBigInt()
                    assertEquals(expected, aFromString.compareTo(b))
                    assertEquals(expected, aFromString.compareTo(bFromString))
                    assertEquals(expected, aFromString.compareTo(bFromNumber))
                    assertEquals(expected, aFromNumber.compareTo(b))
                    assertEquals(expected, aFromNumber.compareTo(bFromString))
                    assertEquals(expected, aFromNumber.compareTo(bFromNumber))
                }
                run {
                    val expected = b.compareTo(a)
                    val aFromNumber = a.toBigInt()
                    val aFromString = a.toString().toBigInt()
                    val bFromNumber = b.toBigInt()
                    val bFromString = b.toString().toBigInt()
                    assertEquals(expected, bFromString.compareTo(a))
                    assertEquals(expected, bFromString.compareTo(aFromString))
                    assertEquals(expected, bFromString.compareTo(aFromNumber))
                    assertEquals(expected, bFromNumber.compareTo(a))
                    assertEquals(expected, bFromNumber.compareTo(aFromString))
                    assertEquals(expected, bFromNumber.compareTo(aFromNumber))
                }
            }
        }
    }

    @Test
    fun testCompareBig() {
        val aString =
            ""
        val bString =
            ""

        val a = aString.toBigInt()
        val b = bString.toBigInt()
        assertTrue(b > a)
        assertTrue(b > 0)
        assertTrue(a > 0)

        val bInvString = "-$bString"
        val bInv = bInvString.toBigInt()

        assertTrue(bInv < a)
        assertTrue(bInv < 0)
    }
}
